import React, { Component, useState } from "react";
import avatar from "./images/icon-badge-grove-2.svg";

import { css } from "emotion";

const Donation = (props) => {
  return (
    <div
      className={css`
        display: flex;
        flex-direction: row;
        color: #273654;
        background-color: #f6f6f4;
        padding: 1rem;
        border-bottom: 1px solid #eee;
        text-align: left !important;
        box-sizing: border-box;
        font-size: 1rem;
        font-weight: 600;
        height: 120px;
        position: relative;
      `}
    >
      <img
        src={avatar}
        alt="avatar"
        className={css`
          height: 100%;
        `}
      />

      <span
        className={css`
          display: flex;
          flex-direction: column;
          margin-left: 1rem;
          text-align: center;
        `}
      >
        <span
          className={css`
            display: flex;
            flex-direction: row;
            height: 50%;
            position: relative;
          `}
        >
          <span
            className={css`
              font-size: 24px;
              display: flex;
              justify-content: center;
              align-items: center;
            `}
          >
            {props.name}
          </span>

          <span
            className={css`
              background-color: #4b7330;
              border-radius: 1rem;
              margin-left: 1rem;
              padding: 0rem 0.5rem 0rem 0.5rem;
              color: #fff;
              display: flex;
              justify-content: center;
              align-items: center;
            `}
          >
            {props.numTrees} trees
          </span>
        </span>

        <span
          className={css`
            display: flex;
            justify-content: left;
            align-items: center;
            height: 50%;
          `}
        >
          {props.message}
        </span>
      </span>

      {/* <div
        className={css`
          height: 50%;
        `}
      >
        {props.message}
      </div> */}
    </div>
  );
};

const Leaderboard = () => {
  const [donations, setDonations] = useState([]);

  const addDonation = () => {
    setDonations([
      ...donations,
      <Donation
        name="Austin Chow"
        numTrees="60"
        message="Keep the climate alive"
      />,
    ]);
  };

  return (
    <div>
      <button onClick={addDonation}>Add Donation</button>

      <div
        className={css`
          width: 576px;
        `}
      >
        <div
          className={css`
            display: flex;
            flex-direction: column;
            margin-top: 5rem;
            margin-bottom: 5rem;
          `}
        >
          <div
            className={css`
              border-radius: calc(0.5rem - 1px) calc(0.5rem - 1px) 0 0;
              flex-direction: column;
              color: #273654;
              background-color: #f6f6f4;
              padding: 1rem 1.25rem 0.8rem;
              border-bottom: 1px solid #eee;
              text-transform: uppercase !important;
              text-align: center !important;
              box-sizing: border-box;
              font-size: 1rem;
              font-weight: 600;
            `}
          >
            DONATIONS
          </div>

          {donations}

          <div
            className={css`
              border-radius: 0 0 calc(0.5rem - 1px) calc(0.5rem - 1px);
              background-color: #f6f6f4;
              border-top: 1px solid #eee;
              padding: 0.75rem 1.25rem;
              flex-direction: column;
              font-weight: 400;
              margin-bottom: 0 !important;
              text-transform: uppercase !important;
              font-size: 70%;
              text-align: center !important;
              color: #518360;
            `}
          ></div>
        </div>
      </div>
    </div>
  );
};

export default Leaderboard;
